import React, { useEffect, useState } from "react";

function Search({searchFunction}) {
  const [search, setSearch] = useState("");



  async function searchFunction(searchTerms){
    fetch("http://localhost:8001/transactions")
      .then(res => res.json())
      .then(data => {
        let newData = [];
        const filtered = data.filter(item => {
          return item.description.includes(searchTerms)

        })
        setAccountData(filtered)
        console.log(filtered)
      })
      .catch(error => {
        console.log(error)
      })

  }

  useEffect(() => {
    searchFunction(search)
  }, [search])

  //this will update the input on every change that takes place upon input
  function handleSearch(e){
    e.preventDefault();
    setSearch(e.target.value)
  }
  return (
    <div className="ui large fluid icon input">
      <input 
    className="forml" 
    placeholder="Search Transactions Here" 
    onChange={handleSearch} 
      />
      <i className="circular search link icon"></i>
    </div>
  );
}

export default Search;
